function t(key, vars = {}) {
  const lang = localStorage.getItem("ak_lang") || "en";
  let str =
    (TRANSLATIONS[lang] && TRANSLATIONS[lang][key]) ||
    TRANSLATIONS["en"][key] ||
    key;

  Object.keys(vars).forEach(k => {
    str = str.replace(`{${k}}`, vars[k]);
  });

  return str;
}




// Apply translations
function applyTranslations(lang){
  const dict = TRANSLATIONS[lang] || TRANSLATIONS['en'];
  document.querySelectorAll('[data-key]').forEach(el=>{
    const k = el.getAttribute('data-key');
    if(dict[k]) el.textContent = dict[k];
  });
  document.querySelectorAll('[data-pl]').forEach(el=>{
    const k = el.getAttribute('data-pl');
    if(dict[k]) el.placeholder = dict[k];
  });

  // translate category option text in selects
  ['aw_category','fw_category'].forEach(id=>{
    const sel = document.getElementById(id);
    if(!sel) return;
    Array.from(sel.options).forEach(opt=>{
      const cat = opt.getAttribute('data-cat') || opt.value;
      const key = cat.toLowerCase().replace(/\s+/g,'');
      if(dict[key]) opt.textContent = dict[key];
      else opt.textContent = cat;
    });
  });

  // translate about text
  if(dict.aboutTitle) document.querySelectorAll('[data-key="aboutTitle"]').forEach(e=> e.textContent = dict.aboutTitle);
  if(dict.aboutText) document.querySelectorAll('[data-key="aboutText"]').forEach(e=> e.textContent = dict.aboutText);
}

