const LANGS = {en:'English', hi:'рд╣рд┐рдиреНрджреА', mr:'рдорд░рд╛рдареА', bn:'ржмрж╛ржВрж▓рж╛', ur:'╪з╪▒╪п┘И', ta:'родрооро┐ро┤рпН', te:'р░др▒Жр░▓р▒Бр░Чр▒Б', kn:'р▓Хр▓ир│Нр▓ир▓б', pa:'рикрй░риЬри╛римрйА', gu:'ркЧрлБркЬрк░рк╛ркдрлА'};

(function initLangsAndApply() {
  const saved = localStorage.getItem('ak_lang') || 'en';

  const langSelect = document.getElementById('langSelect');
  if (langSelect) {
    // populate selector only if present on the page
    Object.entries(LANGS).forEach(([code, label]) => {
      const o = document.createElement('option');
      o.value = code;
      o.textContent = label;
      langSelect.appendChild(o);
    });
    langSelect.value = saved;

    langSelect.addEventListener('change', (e) => {
      const lang = e.target.value;
      localStorage.setItem('ak_lang', lang);
      location.reload();
    });
  }

  // apply translations for the current page (safe, even if selector is absent)
  if (typeof applyTranslations === 'function') {
    applyTranslations(saved);
  }
})();
